import { useState } from "react"
import { AirtableIntegration } from "./integrations/airtable"
import { NotionIntegration } from "./integrations/notion"
import { HubspotIntegration } from "./integrations/hubspot"
import { DataForm } from "./data-form"

const integrationMapping = {
  Notion: NotionIntegration,
  Airtable: AirtableIntegration,
  Hubspot: HubspotIntegration,
}

export const IntegrationForm = () => {
  const [integrationParams, setIntegrationParams] = useState({})
  const [user, setUser] = useState("TestUser")
  const [org, setOrg] = useState("TestOrg")
  const [currType, setCurrType] = useState(null)
  const CurrIntegration = integrationMapping[currType]

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 to-indigo-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-md mx-auto bg-white rounded-xl shadow-md overflow-hidden md:max-w-2xl">
        <div className="p-8">
          <h2 className="text-2xl font-semibold text-gray-800 mb-6">Integration Setup</h2>
          <div className="space-y-4">
            <input
              type="text"
              placeholder="User"
              value={user}
              onChange={(e) => setUser(e.target.value)}
              className="w-full px-3 py-2 placeholder-gray-400 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
            <input
              type="text"
              placeholder="Organization"
              value={org}
              onChange={(e) => setOrg(e.target.value)}
              className="w-full px-3 py-2 placeholder-gray-400 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
            <select
              onChange={(e) => setCurrType(e.target.value)}
              className="w-full px-3 py-2 text-gray-700 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="">Select Integration Type</option>
              {Object.keys(integrationMapping).map((type) => (
                <option key={type} value={type}>
                  {type}
                </option>
              ))}
            </select>
          </div>
          {currType && (
            <div className="mt-6">
              <CurrIntegration
                user={user}
                org={org}
                integrationParams={integrationParams}
                setIntegrationParams={setIntegrationParams}
              />
            </div>
          )}
          {integrationParams?.credentials && (
            <div className="mt-6">
              <DataForm integrationType={integrationParams?.type} credentials={integrationParams?.credentials} />
            </div>
          )}
        </div>
      </div>
    </div>
  )
}
