import { useState } from "react"
import axios from "axios"

const endpointMapping = {
  Notion: "notion",
  Airtable: "airtable",
  Hubspot: "hubspot",
}

export const DataForm = ({ integrationType, credentials }) => {
  const [loadedData, setLoadedData] = useState(null)
  const endpoint = endpointMapping[integrationType]

  const handleLoad = async () => {
    try {
      const formData = new FormData()
      formData.append("credentials", JSON.stringify(credentials))
      const response = await axios.post(`http://localhost:8000/integrations/${endpoint}/get_hubspot_items`, formData) ;
      const data = response.data ;
      setLoadedData(data)
    } catch (e) {
      alert(e?.response?.data?.detail)
    }
  }

  return (
    <div className="mt-8">
      <h3 className="text-lg font-medium text-gray-900 mb-4">Integration Data</h3>
      <div className="bg-gray-100 rounded-lg p-4 mb-4">
        <pre className="whitespace-pre-wrap text-sm text-gray-700">
          {loadedData ? JSON.stringify(loadedData, null, 2) : "No data loaded"}
        </pre>
      </div>
      <div className="flex space-x-4">
        <button
          onClick={handleLoad}
          className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
        >
          Load Data
        </button>
        <button
          onClick={() => setLoadedData(null)}
          className="px-4 py-2 bg-gray-600 text-white rounded-md hover:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2"
        >
          Clear Data
        </button>
      </div>
    </div>
  )
}
